script:
frappe.ui.form.on('Payment Entry', {
	party(frm) {
		// your code here
		frm.set_value("custom_membership", frm.doc.party)
	},
	validate(frm){
	    frappe.call({
			method: 'library_management.services.rest.update_outstanding_debt',
			args: {
				'party': frm.doc.party
			},
			callback: function(r) {
				frappe.msgprint(r.message)
			}
	});
	},
	refresh(frm){
	     frappe.call({
			method: 'library_management.services.rest.send_email',
			args: {
				'name': frm.doc.name
			},
			callback: function(r) {
				frappe.msgprint(r.message)
			}
	});
	}
})
